package frc.robot.utils;import com.ctre.phoenix.motorcontrol.can.WPI_TalonFX;import com.ctre.phoenix.motorcontrol.can.WPI_TalonSRX;public class Utils {private Utils() {}public static double limit(double v, double maxMagnitude) { return limit(v, -maxMagnitude, maxMagnitude);}public static double limit(double v, double min, double max) {return Math.min(max, Math.max(min, v));}public static double applyDeadband(double val, double deadband){if(Math.abs(val) > deadband){if(val > 0.0){return (val - deadband)/ (1.0 - deadband);} else {return (val + deadband)/ (1.0 - deadband);}} else {return 0.0;}}public static void configMotionMagic(WPI_TalonSRX _talon, int acceleration, int cruiseVelocity){_talon.configMotionCruiseVelocity(cruiseVelocity, 0);_talon.configMotionAcceleration(acceleration, 0);}public static void configPIDF(WPI_TalonSRX _talon, double kP, double kI, double kD, double kF) {_talon.config_kP(0, kP, 0);_talon.config_kI(0, kI, 0);_talon.config_kD(0, kD, 0);_talon.config_kF(0, kF, 0);}public static void configPIDF(WPI_TalonFX _talon, double kP, double kI, double kD, double kF) {_talon.config_kP(0, kP, 0);_talon.config_kI(0, kI, 0);_talon.config_kD(0, kD, 0);_talon.config_kF(0, kF, 0);}}